import { panel, text } from "@metamask/snaps-ui";
import { panelToCode, nodeModelsToComponent } from "./utils";
describe("nodeModelsToComponent", function() {
    it("creates a component from an array of node models", function() {
        var nodeModels = [
            {
                id: 1,
                parent: 0,
                text: "parent",
                data: panel([])
            },
            {
                id: 2,
                parent: 1,
                text: "child",
                data: panel([])
            },
            {
                id: 3,
                parent: 2,
                text: "child",
                data: text("foo")
            }
        ];
        var component = nodeModelsToComponent(nodeModels);
        expect(component).toStrictEqual(panel([
            panel([
                text("foo")
            ])
        ]));
    });
});
describe("paneltoCode", function() {
    it("creates code from a component", function() {
        var component = panel([
            text("foo"),
            panel([
                text("bar"),
                text("baz")
            ])
        ]);
        var code = panelToCode(component);
        expect(code).toMatchInlineSnapshot("\n      \"import { panel, text } from '@metamask/snaps-ui';\n\n      const component = panel([text('foo'), panel([text('bar'), text('baz')])]);\n      \"\n    ");
    });
});
