import { configuration as reducer, INITIAL_CONFIGURATION_STATE, openConfigurationModal, setOpen, setSesEnabled, setSrp, setSnapId } from "./slice";
describe("configuration slice", function() {
    describe("openConfigurationModal", function() {
        it("opens the modal", function() {
            var result = reducer(INITIAL_CONFIGURATION_STATE, openConfigurationModal());
            expect(result.open).toBe(true);
        });
    });
    describe("setOpen", function() {
        it("sets the open state", function() {
            var result = reducer(INITIAL_CONFIGURATION_STATE, setOpen(true));
            expect(result.open).toBe(true);
        });
    });
    describe("setSnapId", function() {
        it("sets the snap URL", function() {
            var id = "local:http://localhost:9090";
            var result = reducer(INITIAL_CONFIGURATION_STATE, setSnapId(id));
            expect(result.snapId).toStrictEqual(id);
        });
    });
    describe("setSrp", function() {
        it("sets the SRP", function() {
            var srp = "test test test test test test test test test test test ball";
            var result = reducer(INITIAL_CONFIGURATION_STATE, setSrp(srp));
            expect(result.srp).toStrictEqual(srp);
        });
    });
    describe("setSesEnabled", function() {
        it("sets the SES enabled flag", function() {
            var result = reducer(INITIAL_CONFIGURATION_STATE, setSesEnabled(false));
            expect(result.sesEnabled).toBe(false);
        });
    });
});
