import { INITIAL_MANIFEST_STATE, manifest as reducer, ManifestStatus, setResults, setValid } from "./slice";
describe("manifest slice", function() {
    describe("setValid", function() {
        it("sets the status of the manifest", function() {
            var result = reducer(INITIAL_MANIFEST_STATE, setValid(ManifestStatus.Valid));
            expect(result.valid).toStrictEqual(ManifestStatus.Valid);
        });
    });
    describe("setResults", function() {
        it("sets the validation results", function() {
            var result = reducer(INITIAL_MANIFEST_STATE, setResults([
                {
                    name: "foo",
                    manifestName: "bar",
                    isValid: true
                }
            ]));
            expect(result.results).toStrictEqual([
                {
                    name: "foo",
                    manifestName: "bar",
                    isValid: true
                }
            ]);
        });
    });
});
